import Echo from 'laravel-echo';
import Pusher from 'pusher-js';
 
window.Pusher = Pusher;
 
window.Echo = new Echo({
    broadcaster: 'pusher',
    key: "f02f767c4e4305be4576",
    cluster: "ap1",
    forceTLS: true,
    authEndpoint: '/Sapo/broadcasting/auth'
});

